import { useState } from "react";
import "./Clock.css";

function Clock(): JSX.Element {

    const [time, setTime] = useState("");
    useEffect(() => {
        setInterval(() => {
            const now = new Date ();
            setTimeout(now.toLocaleTimeString())
        }, 1000);

    }, [])


    return (
        <div className="Clock">
			{time}
        </div>
    );
}

export default Clock;
