import "./Specials.css";

function Specials(): JSX.Element {
    return (
        <div className="Specials Box">
			<p>Our Specials:
                {isWeekend()? <span>Pi</span> : <span></span>}
                <span>Pizza</span>
                <span>Pasta</span>
            </p>
        </div>
    );
}

 function isWeekend(): boolean {
    const now = new Date();
    const dayOfWeek = now.getDay() + 1;
    return dayOfWeek >= 6;
    }

export default Specials;
